mapper.update:
update vote_count
set item_id = #{itemId,jdbcType=VARCHAR},
  vote_user_id = #{voteUserId,jdbcType=VARCHAR},
  vote_topic_id = #{voteTopicId,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, item_id, vote_user_id, vote_topic_id
from vote_count
where id = #{id,jdbcType=VARCHAR}
